import React, { useEffect, useState } from "react";
import axios from "axios";
import {
  Row,
  Col,
  Image,
  Accordion,
  Button,
  ListGroup,
  ProgressBar,
  Carousel,
  InputGroup,
  FormControl
} from "react-bootstrap";
import { FaSearch } from "react-icons/fa";
import Recurso from "../Img/Recursos.svg";
import NBoletin from "../Img/NBoletin.png";
import NLogros from "../Img/NLogros.png";
import NRecuerda from "../Img/NRecuerda.png";
import { useNavigate } from "react-router-dom";
import "./TalentoMejorada.css";

export default function Inicio() {
  const url = process.env.REACT_APP_API_BACK + "/sesiones/";

  const [sesiones, setSesiones] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    const getSesiones = async () => {
      try {
        const response = await axios.get(url);
        setSesiones(response.data.results);
      } catch (error) {
        console.error("Error fetching sesiones:", error);
      }
    };

    getSesiones();
  }, [url]);

  // Funci贸n para manejar la navegaci贸n y asignar variables de sesi贸n
  const validar = (ev, IdCurso, IdSesion) => {
    ev.preventDefault(); // Evitar que el formulario se recargue al presionar el bot贸n
    sessionStorage.setItem("IdCurso", IdCurso);
    sessionStorage.setItem("IdSesion", IdSesion);
    navigate("../talentomejorada/asistenciassesiones");
  };

  const crearAsistencia = async (IdCurso,IdSesion,IdUsuario) => {
    try {
      // const response = await axios.delete(`http://localhost:8080/usuario/${idUsuario}`);
      const url = `${process.env.REACT_APP_API_BACK}/sesionesusuario/crear`;

      const response = await axios.post(url, {
        IdCurso: IdCurso,
        IdSesion: IdSesion,
        IdUsuario: IdUsuario
      });


      console.log('asistencia creada:', response.data);
     
    } catch (error) {
      console.error('Error creando asistencia:', error);
    }
  };


  return (
    <div>
      <Col>
        <br />
        <h2 className="text-blue-600/100 text-center">Desarrollo Web Full Stack</h2>

        <InputGroup className="mb-3" style={{ maxWidth: "400px", maxHeight: "20px" }}>
          <FormControl
            placeholder="Search"
            aria-label="Search"
            aria-describedby="basic-addon2"
          />
          <br />
          <Button variant="outline-secondary" id="button-addon2">
            <FaSearch />
          </Button>
        </InputGroup>

        <strong>Desarrollo del bootcamp:</strong>
        <br />
        <Row>
          <Col
            sm={12}
            md="10"
            lg="8"
            style={{ maxHeight: "500px", overflowY: "auto" }}
            className="bg-gradient-to-r from-fuchsia-50"
          >
            <ProgressBar
              striped
              variant="primary"
              now={70}
              label={`${70}%`}
              rounded
            />

            <Accordion flush className="p-3">
              {sesiones.map((sesion) => {
                const fecha = new Date(sesion.FechaSesion);
                const opciones = {
                  year: "numeric",
                  month: "long",
                  day: "numeric",
                };
                const fechaFormateada = new Intl.DateTimeFormat("es-ES", opciones).format(fecha);

                let estadoButtonVariant = "";
                switch (sesion.Estado) {
                  case "Finalizado":
                    estadoButtonVariant = "danger";
                    break;
                  case "EnCurso":
                    estadoButtonVariant = "success";
                    break;
                  default:
                    estadoButtonVariant = "warning";
                    break;
                }

                return (
                  <Accordion.Item
                    key={sesion.IdSesion}
                    eventKey={sesion.IdSesion}
                    className="item-acordeon"
                  >
                    <Accordion.Header>
                      <div className="d-flex justify-content-between align-items-center w-100">
                        <div>
                          <span>
                            <strong>{sesion.NombreSesion}</strong> {sesion.DescripcionSesion}
                          </span>
                          <br />
                          <span>{fechaFormateada}</span>
                        </div>
                        <div>
                          <Button
                            variant={estadoButtonVariant}
                            size="sm"
                            className="oval-button"
                          >
                            {sesion.Estado}
                          </Button>
                        </div>
                      </div>
                    </Accordion.Header>
                    <Accordion.Body>
                      Horario: {sesion.Horario}
                      <br />
                      Asistencia: Asistencia Marcada
                      <br />
                      Descripcion: {sesion.DescripcionSesion}
                      <br />
                      <Button
                        variant="primary"
                        size="sm"
                        rounded
                        className="me-2"
                      >
                        Ingresar a la grabaci贸n
                      </Button>
                      <Button
                        variant="primary"
                        size="sm"
                        rounded
                        onClick={(ev) => validar(ev, sesion.IdCurso, sesion.IdSesion)}
                      >
                        Ver asistentes
                      </Button                      >
                     
                      <a
                      onClick={() => {
                          crearAsistencia(sesion.IdCurso, sesion.IdSesion,sessionStorage.getItem("IdUsuario"));
                        
                      }}
                        href={sesion.LinkSesion}
                        className="btn btn-primary btn-sm rounded"
                        target="_blank"
                        rel="noopener noreferrer" // Agrega rel para seguridad
                      >
