import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

export default function Inicio() {
  const navigate = useNavigate();
  const [formularioActivo, setFormularioActivo] = useState(null);
  const [menuAbierto, setMenuAbierto] = useState(false);
  const rolUsuario = sessionStorage.getItem("Rol") || "No definido";

  const handleLogout = () => {
    sessionStorage.clear();
    navigate("/login");
  };

  return (
    <div className="flex min-h-screen flex-col md:flex-row">
      {/* Botón menú hamburguesa para móviles */}
      <button
        onClick={() => setMenuAbierto(!menuAbierto)}
        className="md:hidden p-4 bg-blue-900 text-white font-bold"
      >
        ☰ Menú
      </button>

      {/* Menú lateral */}
      <aside
        className={`bg-blue-900 text-white p-4 space-y-4 z-10 md:relative fixed top-0 left-0 h-full w-64 md:w-1/5 transform transition-transform duration-300 ease-in-out md:translate-x-0 ${
          menuAbierto ? "translate-x-0" : "-translate-x-full"
        }`}
      >
        <h2 className="text-xl font-bold mb-4">Menú</h2>
        {[
          "Empleados",
          "Actividades",
          "Máquinas y Equipos",
          "Materiales",
          "Proyectos",
          "Áreas",
          "Cargos de empleados",
          "Nuevo reporte de obra",
        ].map((form, idx) => (
          <button
            key={idx}
            onClick={() => {
              setFormularioActivo(form);
              setMenuAbierto(false);
            }}
            className="block w-full text-left px-2 py-1 bg-blue-800 hover:bg-blue-700 rounded"
          >
            {form}
          </button>
        ))}
        <button
          onClick={handleLogout}
          className="bg-blue-500 hover:bg-blue-400 text-white font-bold py-2 px-4 border-b-4 border-blue-700 hover:border-blue-500 rounded mt-4"
        >
          Cerrar sesión
        </button>
      </aside>

      {/* Contenido principal */}
      <main className="flex-1 flex flex-col md:flex-row p-4 gap-4">
        <div className="flex-1 bg-white p-6 rounded shadow-md">
          {formularioActivo ? (
            <div>
              <h2 className="text-lg font-semibold mb-4">{formularioActivo}</h2>
              <input
                type="text"
                placeholder="Nombre del proyecto"
                className="w-full border p-2 mb-2 rounded"
              />
              <input
                type="date"
                className="w-full border p-2 mb-4 rounded"
              />
              <button className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700">
                Guardar
              </button>
            </div>
          ) : (
            <div className="text-center text-gray-500 font-bold mt-20">
              aca el dashboard
            </div>
          )}
        </div>

        {/* Panel lateral derecho con rol */}
        <div className="w-full md:w-1/4">
          <div className="bg-white rounded-xl shadow-lg p-4">
            <h3 className="text-lg font-semibold mb-2">Dashboard</h3>
            <p>Progreso: 70%</p>
            <p>Último reporte: ayer</p>
            <p>Total actividades: 12</p>
            <div className="mt-4 border-t pt-2">
              <h4 className="font-bold">Rol del usuario</h4>
              <p className="text-blue-700">{rolUsuario}</p>
            </div>
          </div>
        </div>
      </main>
    </div>
  );
}
